import zhMessages from "../locales/zh-Hans.json";

const zh_text =
  "输新密码并进行确认用户名密码或者点击任意按钮未读电子邮件电子邮件地址查看购物车查看全部我的简历";

let text = "Hello world!";

console.log("slice text: ", text.slice(0, 3));

zhMessages["sidebar.publisher"] = "输入新";

const Zhlang = (recipeValues) => {
  const uniqueRecipes = recipeValues
    ?.map((recipe) => recipe.publisher)
    .filter((value, index, self) => self.indexOf(value) === index)
    .map((_recipe) => {
      const recipe = _recipe.replaceAll(" ", "-").toLowerCase();
      return {
        key: _recipe,
        label: `sidebar.${recipe}`,
      };
    });

  if (recipeValues) {
    uniqueRecipes.forEach((recipe) => {
      const wordLength = recipe.key.split(" ").length;

      const number = zh_text.length - wordLength;

      console.log("wordLength: ", wordLength);
      const randomNumber = Math.floor(Math.random() * number);

      zhMessages[recipe?.label] = zh_text.slice(randomNumber, wordLength);
    });
  }

  const ZhLan_data = {
    messages: {
      ...zhMessages,
    },
    antd: null,
    locale: "zh-Hans-CN",
  };

  return ZhLan_data;
};

export default Zhlang;
