import React from "react";
import { useSelector } from "react-redux";

import { Modal } from "antd";
import { useDispatch } from "react-redux";
import "./Recipe.css";

const ReturnModal = () => {
  const dispatch = useDispatch();
  const { detailRecipe, status } = useSelector((state) => state.detailRecipe);

  console.log("detailRecipe: ", detailRecipe);

  const cancelHandle = () => {
    dispatch({ type: "CLOSE_RECIPE" });
  };

  return (
    <Modal
      wrapClassName=""
      visible={true}
      //   visible={status}
      onCancel={cancelHandle}
      footer={null}
    >
      {/* <div>{detailRecipe?.publisher}</div> */}
      <div style={{ height: "650px", backgroundColor: "#ff00001f" }}>
        <h2 class="heading--2">Recipe ingredients</h2>
        <div class="recipe__details">
          <div class="recipe__info">
            <svg class="recipe__info-icon">
              <use href="https://forkify-v2.netlify.app/icons.c781f215.svg#icon-clock"></use>
            </svg>
            <span class="recipe__info-data recipe__info-data--minutes">75</span>
            <span class="recipe__info-text">minutes</span>
          </div>
          <div class="recipe__info">
            <svg class="recipe__info-icon">
              <use href="https://forkify-v2.netlify.app/icons.c781f215.svg#icon-users"></use>
            </svg>
            <span class="recipe__info-data recipe__info-data--people">4</span>
            <span class="recipe__info-text">servings</span>

            <div class="recipe__info-buttons">
              <button class="btn--tiny btn--update-servings" data-update-to="3">
                <svg>
                  <use href="https://forkify-v2.netlify.app/icons.c781f215.svg#icon-minus-circle"></use>
                </svg>
              </button>
              <button class="btn--tiny btn--update-servings" data-update-to="5">
                <svg>
                  <use href="https://forkify-v2.netlify.app/icons.c781f215.svg#icon-plus-circle"></use>
                </svg>
              </button>
            </div>
          </div>

          <div class="recipe__user-generated hidden">
            <svg>
              <use href="https://forkify-v2.netlify.app/icons.c781f215.svg#icon-user"></use>
            </svg>
          </div>
          <button class="btn--round btn--bookmark">
            <svg class="">
              <use href="https://forkify-v2.netlify.app/icons.c781f215.svg#icon-bookmark"></use>
            </svg>
          </button>
        </div>
      </div>
    </Modal>
  );
};

export default ReturnModal;
