import React from "react";

const Body = ({ ingredients }) => {
  const randomnum = Math.floor(Math.random() * 5);
  const color = ["#f38e82", "#829ff3", "#e782f3", "#93f382", "#ed82f3"];

  return (
    <>
      <h2 style={{ color: color[randomnum] }} className="heading--2">
        Recipe ingredients
      </h2>
      <div
        style={{
          display: "flex",
          flexWrap: "wrap",
          width: "100%",
          // backgroundColor: "red",
        }}
      >
        {ingredients.map((ingre) => {
          return (
            <div style={{ width: "50%", paddingBottom: "30px" }}>{ingre}</div>
          );
        })}
      </div>
    </>
  );
};

export default Body;
