import React from "react";
import { IoIosTimer, IoMdPersonAdd } from "react-icons/io";
// import { IoPersonAddOutline } from "react-icons/io5";

const Head = ({ title }) => {
  const styleIcon = { fontSize: "20px", color: "#f38e82" };
  const alignCenter = {
    display: "flex",
    alignItems: "center",
    paddingRight: "20px",
  };

  const randomnum = Math.floor(Math.random() * 5);
  const color = ["#f38e82", "#829ff3", "#e782f3", "#93f382", "#ed82f3"];

  return (
    <>
      <h1 className="recipe__title">
        <span style={{ color: color[randomnum] }}>{title.slice(0, 19)}</span>
      </h1>
      <div style={{ display: "flex", marginLeft: "20px", paddingTop: "60px" }}>
        <div style={alignCenter}>
          <IoIosTimer style={styleIcon} /> &nbsp; 75 MINUTE
        </div>
        <div style={alignCenter}>
          <IoMdPersonAdd style={styleIcon} /> &nbsp; 4 SERVINGS
        </div>
      </div>
    </>
  );
};

export default Head;
