import antdEn from "antd/lib/locale-provider/en_US";
import enMessages from "../locales/en_US.json";

enMessages["sidebar.recipes"] = "Recipes";

const EnLang = (recipeValues) => {
  const uniqueRecipes = recipeValues
    .map((recipe) => recipe.publisher)
    .filter((value, index, self) => self.indexOf(value) === index)
    .map((_recipe) => {
      const recipe = _recipe.replaceAll(" ", "-").toLowerCase();
      return {
        key: recipe,
        label: `sidebar.${recipe}`,
      };
    });

  if (recipeValues) enMessages[recipeValues?.label] = recipeValues?.key;

  const EnLang_data = {
    messages: {
      ...enMessages,
    },
    antd: antdEn,
    locale: "en-US",
  };

  return EnLang_data;
};

export default EnLang;
